def convert_format(old_item, catalog_code):
    """
    Convierte formato antiguo a nuevo:
    - 'price' → 'sale_price'
    - 'cost' → 'cost_price'
    - Agrega 'catalog_code'
    """
    return {
        "name": old_item["name"],
        "sale_price": old_item.get("price", old_item.get("sale_price")),
        "cost_price": old_item.get("cost", old_item.get("cost_price", 0)),
        "unit": old_item.get("unit", "unidad"),
        "category": old_item["category"],
        "aliases": old_item.get("aliases", []),
        "brand": old_item.get("brand"),
        "barcode": old_item.get("barcode"),
        "catalog_code": catalog_code
    }


def save_catalog_to_json(catalog_list, catalog_code, catalog_name):
    """Guarda catálogo como JSON"""
    
    # Convertir formato
    converted = [convert_format(item, catalog_code) for item in catalog_list]
    
    # Crear carpeta si no existe
    os.makedirs('app/data/catalogs', exist_ok=True)
    
    # Guardar JSON
    filepath = f'app/data/catalogs/{catalog_code}.json'
    with open(filepath, 'w', encoding='utf-8') as f:
        json.dump(converted, f, ensure_ascii=False, indent=2)
    
    print(f"✅ {catalog_name}: {len(converted)} productos → {filepath}")


